}

function getUser() {
    // get user from local storage
    var user = localStorage.getItem('user');
    if (!user) {
        user = 'admin';
        localStorage.setItem('user', user);
    }
    return user;
}

function getPicks(user) {
    var picks = JSON.parse(localStorage.getItem(user));
    if (!picks) {
        picks = {};
    }
    return picks;
}

